INSERT INTO `wp_posts` VALUES (2,1,'2023-03-28 07:53:38','2023-03-28 07:53:38','<!-- wp:acf/about {\"name\":\"acf/about\",\"data\":{\"background_img_about\":288,\"_background_img_about\":\"field_643faf2367542\",\"about_info\":\"It all began with an unexpected discovery. CureVac’s founder, Dr. Ingmar Hoerr (a doctoral student at the time), discovered that when it was administered directly into tissue, the notoriously unstable biomolecule mRNA could be used as a therapeutic vaccine or agent after optimization—no complicated reformulations or molecular packaging needed. The radical discovery that scientists could actualize the potential of mRNA—a molecule found inside each and every person—for therapeutic  purposes would change biomedical research forever.\",\"_about_info\":\"field_643fcaa53bddd\",\"info_title\":\"We built CureVac from life’s building blocks\",\"_info_title\":\"field_643fcad587a7a\",\"about_info_second\":\"With a single discovery, CureVac opened the world up to the potential of mRNA to treat diseases and create vaccines. Officially founded in 2000, CureVac is the world’s first company to successfully harness mRNA for medical purposes—because we saw opportunities where others saw obstacles. Today, we’re more than 400 passionate people strong, each one committed to using the clinical potential of our proprietary mRNA technology to provide tailored solutions for those with the greatest medical needs.\",\"_about_info_second\":\"field_643fcaef87a7b\",\"woman_image\":283,\"_woman_image\":\"field_643fcb1687a7c\",\"box_info\":{\"title\":\"Spotlight on CureVac the RNA people\",\"url\":\"#\",\"target\":\"\"},\"_box_info\":\"field_643fcb3687a7d\",\"info_title_second\":\"Meet the leadership team\",\"_info_title_second\":\"field_643fcb8067069\",\"about_info_third\":\"These passionate CureVac partners are leading the way for our revolutionary mRNA therapies to transform the lives of  patients around the world.\",\"_about_info_third\":\"field_643fcbaf6706a\",\"hero_mobile_image\":346,\"_hero_mobile_image\":\"field_6447a7b44b06f\",\"career_info_contents\":\"\",\"_career_info_contents\":\"field_64469064ca0d4\"},\"mode\":\"auto\"} /-->\n\n<!-- wp:acf/position {\"name\":\"acf/position\",\"mode\":\"auto\"} /-->','About us','','publish','closed','closed','','about-us','','','2023-04-25 11:58:13','2023-04-25 11:58:13','',0,'http://cure-vac.local/?page_id=2',0,'page','',0);
INSERT INTO `wp_posts` VALUES (8,1,'2023-03-28 08:26:43','2023-03-28 08:26:43','<!-- wp:paragraph -->\n<p>This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...or something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>As a new WordPress user, you should go to <a href=\"http://cure-vac.local/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!</p>\n<!-- /wp:paragraph -->','About Us','','inherit','closed','closed','','2-revision-v1','','','2023-03-28 08:26:43','2023-03-28 08:26:43','',2,'http://cure-vac.local/?p=8',0,'revision','',0);
