INSERT INTO `wp_posts` VALUES (344,1,'2023-04-25 10:13:54','2023-04-25 10:13:54','<!-- wp:acf/about {\"name\":\"acf/about\",\"data\":{\"background_img_about\":288,\"_background_img_about\":\"field_643faf2367542\",\"about_info\":\"It all began with an unexpected discovery. CureVac’s founder, Dr. Ingmar Hoerr (a doctoral student at the time), discovered that when it was administered directly into tissue, the notoriously unstable biomolecule mRNA could be used as a therapeutic vaccine or agent after optimization—no complicated reformulations or molecular packaging needed. The radical discovery that scientists could actualize the potential of mRNA—a molecule found inside each and every person—for therapeutic  purposes would change biomedical research forever.\",\"_about_info\":\"field_643fcaa53bddd\",\"info_title\":\"We built CureVac from life’s building blocks\",\"_info_title\":\"field_643fcad587a7a\",\"about_info_second\":\"With a single discovery, CureVac opened the world up to the potential of mRNA to treat diseases and create vaccines. Officially founded in 2000, CureVac is the world’s first company to successfully harness mRNA for medical purposes—because we saw opportunities where others saw obstacles. Today, we’re more than 400 passionate people strong, each one committed to using the clinical potential of our proprietary mRNA technology to provide tailored solutions for those with the greatest medical needs.\",\"_about_info_second\":\"field_643fcaef87a7b\",\"woman_image\":283,\"_woman_image\":\"field_643fcb1687a7c\",\"box_info\":{\"title\":\"Spotlight on CureVac the RNA people\",\"url\":\"#\",\"target\":\"\"},\"_box_info\":\"field_643fcb3687a7d\",\"info_title_second\":\"Meet the leadership team\",\"_info_title_second\":\"field_643fcb8067069\",\"about_info_third\":\"These passionate CureVac partners are leading the way for our revolutionary mRNA therapies to transform the lives of  patients around the world.\",\"_about_info_third\":\"field_643fcbaf6706a\",\"hero_mobile_image\":342,\"_hero_mobile_image\":\"field_6447a7b44b06f\",\"career_info_contents\":\"\",\"_career_info_contents\":\"field_64469064ca0d4\"},\"mode\":\"auto\"} /-->\n\n<!-- wp:acf/position {\"name\":\"acf/position\",\"mode\":\"auto\"} /-->','About us','','inherit','closed','closed','','2-revision-v1','','','2023-04-25 10:13:54','2023-04-25 10:13:54','',2,'http://cure-vac.local/?p=344',0,'revision','',0);
