INSERT INTO `wp_posts` VALUES (352,1,'2023-04-25 12:25:10','2023-04-25 12:25:10','<!-- wp:acf/careers {\"name\":\"acf/careers\",\"data\":{\"background_image\":324,\"_background_image\":\"field_6446381d5bbcb\"},\"mode\":\"auto\"} /-->\n\n<!-- wp:acf/contents {\"name\":\"acf/contents\",\"data\":{\"career_info_contents_0_tab_title\":\"Work With Us\",\"_career_info_contents_0_tab_title\":\"field_6446908dca0d5\",\"career_info_contents_0_tab_content\":\"From day one, people have always been central to CureVac’s mission—after all, we are on a mission to use therapeutic mRNA to transform people’s lives. That’s why we are dedicated to making CureVac a rewarding place to work for the people who help us get that much closer to making our mission a reality. And since our mission is no small one, we are looking for ambitious and passionate people who share our core values to join us in making a difference in the world. \\r\\n\\r\\nOur core values\\r\\n\\r\\nBeyond our desire to dramatically transform the lives of patients through therapeutic mRNA, everyone at CureVac comes together through our shared values:\\r\\n\",\"_career_info_contents_0_tab_content\":\"field_644690b9ca0d6\",\"career_info_contents_0_icon_contents_0_icon\":350,\"_career_info_contents_0_icon_contents_0_icon\":\"field_6446911bca0d8\",\"career_info_contents_0_icon_contents_0_title\":\"Responsibility \\u0026 Respectsi\",\"_career_info_contents_0_icon_contents_0_title\":\"field_64469124ca0d9\",\"career_info_contents_0_icon_contents_0_description\":\"At CureVac, people matter. For patients, this means we ensure all research and clinical trials are performed with the highest scientific and ethical standards—but it also means we highly value each employee’s effort and dedication.\",\"_career_info_contents_0_icon_contents_0_description\":\"field_6446912bca0da\",\"career_info_contents_0_icon_contents_1_icon\":351,\"_career_info_contents_0_icon_contents_1_icon\":\"field_6446911bca0d8\",\"career_info_contents_0_icon_contents_1_title\":\"Community \\u0026 Progress\",\"_career_info_contents_0_icon_contents_1_title\":\"field_64469124ca0d9\",\"career_info_contents_0_icon_contents_1_description\":\"Shaping the future of medicine isn’t easy—and we believe we can only achieve this ambitious goal as a team. Moving medicine forward will require the specific talents and skills of each person contributing to the CureVac cause.\",\"_career_info_contents_0_icon_contents_1_description\":\"field_6446912bca0da\",\"career_info_contents_0_icon_contents\":2,\"_career_info_contents_0_icon_contents\":\"field_64469101ca0d7\",\"career_info_contents\":1,\"_career_info_contents\":\"field_64469064ca0d4\"},\"mode\":\"auto\"} /-->','Careers','','inherit','closed','closed','','13-revision-v1','','','2023-04-25 12:25:10','2023-04-25 12:25:10','',13,'http://cure-vac.local/?p=352',0,'revision','',0);
